//useEffect = component DID mount => called when component is mounted on DOM
//useState = for storing we use this => 
import { useEffect, useState } from "react";

import { Button, Table, TableBody, TableCell, TableHead, TableRow, styled } from "@mui/material";

import { getUsers, deleteUser } from "../service/api";

//Link to route from all to EditUser when clicking Edit button
import { Link } from "react-router-dom";


const StyledTable = styled(Table)`
    width : 90%;
    margin : 50px auto 0 auto;
`
const THead = styled(TableRow)`
    background : #000;
    & > th {
        color : #fff;
        font-size : 20px;
    }
`
const TBody = styled(TableRow)`
    & > td {
        font-size : 15px;
    }
`

const AllUser = () => {

    const [users, setUsers] = useState([]);

    useEffect(() => {
        getUsersDetails();
    }, [])

    const getUsersDetails = async ()    => {
        let response = await getUsers();
        console.log(response);
        setUsers(response.data);
    }

    const deleteUserData = async (id) => {
        await deleteUser(id);
    }

    return (
        <StyledTable>
            <TableHead>
                <THead>
                    <TableCell>Id</TableCell>
                    <TableCell>Name</TableCell>
                    <TableCell>Username</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>Phone</TableCell>
                    <TableCell></TableCell>
                </THead>
            </TableHead>
            <TableBody>
                {
                    users.map(user =>(
                        <TBody>
                            <TableCell>{user.id}</TableCell>
                            <TableCell>{user.name}</TableCell>
                            <TableCell>{user.username}</TableCell>
                            <TableCell>{user.email}</TableCell>
                            <TableCell>{user.phone}</TableCell>
                            <TableCell>
                                <Button variant="contained" style={{ marginRight: 20 }} component={Link} to={`/edit/${user.id}`} >Edit</Button>
                                <Button variant="contained" color="secondary" onClick={ () => deleteUserData(user.id) } >Delete</Button>
                            </TableCell>
                        </TBody>
                    ))
                }
            </TableBody>
        </StyledTable>
    )
}
    

export default AllUser;
